import React, { useRef, useEffect, useState } from 'react';
import mapboxgl from '!mapbox-gl'; // eslint-disable-line import/no-webpack-loader-syntax
import axios from 'axios'
import './App.css';

mapboxgl.accessToken = 'pk.eyJ1IjoiaWFua2hvdSIsImEiOiJja3U1enMzNHE0enN4Mm9waXNwcDlyeGVpIn0.gz1TKA2H5JfDkMiHkdVxRQ';


export default function App() {
  // update users from Google Sheet
  const mapContainer = useRef(null);
  const map = useRef(null);
  const [users, setUsers] = useState(null);
  const [lng, setLng] = useState(-98.08226562598172);
  const [lat, setLat] = useState(39.67197697791948);
  const [zoom, setZoom] = useState(3);

  useEffect(() => {
    if (users) return;
    axios.get('https://script.google.com/macros/s/AKfycbxrlGRaZXCdV3OTp2TX0HeSOoPrpNGQFYuzl8_pZoIwFcSDm3rDOlBu5L6FMXn4rz-wBA/exec')
      .then((resp) => {
        console.log(resp.data)
        setUsers(resp.data)
      })
    //   fetch("https://script.google.com/macros/s/AKfycbxrlGRaZXCdV3OTp2TX0HeSOoPrpNGQFYuzl8_pZoIwFcSDm3rDOlBu5L6FMXn4rz-wBA/exec", {
    //     method: 'GET',
    //     headers: {
    //         'Content-Type': 'text/plain;charset=utf-8',
    //     }
    // }).then(response => {
    //     console.log("success:", response.data);
    // }).catch(err => {
    //     console.log("Error:" + err);
    // });
  })


  useEffect(() => {
    if (map.current) return; // initialize map only once

    map.current = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [lng, lat],
      zoom: zoom
    });

    map.current.on('load', () => {
      map.current.addSource('trees', {
        type: 'geojson',
        data: './trees.geojson'
      });
      // add heatmap layer here
      // add circle layer here

      map.current.addLayer(
        {
          id: 'trees-heat',
          type: 'heatmap',
          source: 'trees',
          maxzoom: 14,
          paint: {
            // increase weight as diameter breast height increases
            'heatmap-weight': {
              // property: 'dbh',
              type: 'exponential',
              stops: [
                [1, 0],
                [500, 30],
              ]
            },
            // increase intensity as zoom level increases
            'heatmap-intensity': {
              stops: [
                [11, 1],
                [15, 3]
              ]
            },
            // assign color values be applied to points depending on their density
            'heatmap-color': [
              'interpolate',
              ['linear'],
              ['heatmap-density'],
              0,
              'rgba(153,255,153,0)',
              0.2,
              'rgb(0,255,0)',
              0.4,
              'rgb(0,204,0)',
              0.6,
              'rgb(0,153,0)',
              0.8,
              'rgb(0,102,0)'
            ],
            // increase radius as zoom increases
            'heatmap-radius':
            {
              stops: [
                [11, 15],
                [15, 20]
              ]
            },
            // decrease opacity to transition into the circle layer
            'heatmap-opacity': {
              default: 1,
              stops: [
                [14, 1],
                [15, 0]
              ]
            }
          }
        },
        'waterway-label'
      );

      map.current.addLayer(
        {
          id: 'trees-point',
          type: 'circle',
          source: 'trees',
          minzoom: 12,
          paint: {
            // increase the radius of the circle as the zoom level and dbh value increases
            'circle-radius':
            {
              property: 'dbh',
              type: 'exponential',
              stops: [
                [{ zoom: 15, value: 1 }, 20],
                [{ zoom: 15, value: 62 }, 40],
                [{ zoom: 22, value: 1 }, 80],
                [{ zoom: 22, value: 62 }, 200]
              ]
            },
            'circle-color': {
              property: 'dbh',
              type: 'exponential',
              stops: [
                [0, 'rgb(0,153,0)'],
                [10, 'rgb(0,153,0)'],
                [20, 'rgb(0,153,0)'],
                [30, 'rgb(0,153,0)'],
                [40, 'rgb(0,153,0)'],
                [50, 'rgb(0,153,0)'],
                [60, 'rgb(0,153,0)'],
              ]
            },
            'circle-stroke-color': 'white',
            'circle-stroke-width': 2,
            'circle-opacity': {
              stops: [
                [14, 0],
                [15, 1]
              ]
            }
          }
        },
        'waterway-label'
      );

      map.current.on('click', 'trees-point', ({ features }) => {
        new mapboxgl.Popup()
          .setLngLat(features[0].geometry.coordinates)
          .setHTML(
            // `<strong>DBH:</strong> ${features[0].properties.dbh}, ${features[0].properties.major}`
            `<div>
              <h1>Ian Hou<h1>
              <p>Major(s): </p>
            </div>`
          )
          .addTo(map.current);
      });
    });
  });

  useEffect(() => {
    if (!map.current) return; // wait for map to initialize
    map.current.on('move', () => {
      setLng(map.current.getCenter().lng.toFixed(4));
      setLat(map.current.getCenter().lat.toFixed(4));
      setZoom(map.current.getZoom().toFixed(2));
    });
  });



  return (
    <div>
    <div className="sidebar">
    Longitude: {lng} | Latitude: {lat} | Zoom: {zoom}
    </div>
    <div ref={mapContainer} className="map-container" />
    </div>
    );


}
