import React, { useRef, useEffect, useState } from 'react';
import mapboxgl from '!mapbox-gl'; // eslint-disable-line import/no-webpack-loader-syntax
import WorldMap from './WorldMap';
import './App.css';

mapboxgl.accessToken = 'pk.eyJ1IjoiaWFua2hvdSIsImEiOiJja3U1enMzNHE0enN4Mm9waXNwcDlyeGVpIn0.gz1TKA2H5JfDkMiHkdVxRQ';


export default function App() {
  // update users from Google Sheet
  const mapContainer = useRef(null);
  const map = useRef(null);
  const [lng, setLng] = useState(-98.08226562598172);
  const [lat, setLat] = useState(39.67197697791948);
  const [zoom, setZoom] = useState(3);

  useEffect(() => {
    if (map.current) return; // initialize map only once

    map.current = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [lng, lat],
      zoom: zoom
    });

    map.current.on('load', () => {
      map.current.addSource('trees', {
        type: 'geojson',
        data: './trees.geojson'
      });
      // add heatmap layer here
      // add circle layer here

      map.current.addLayer(
        {
          id: 'trees-heat',
          type: 'heatmap',
          source: 'trees',
          maxzoom: 15,
          paint: {
            // increase weight as diameter breast height increases
            'heatmap-weight': {
              property: 'dbh',
              type: 'exponential',
              stops: [
                [1, 0],
                [62, 1]
              ]
            },
            // increase intensity as zoom level increases
            'heatmap-intensity': {
              stops: [
                [11, 1],
                [15, 3]
              ]
            },
            // assign color values be applied to points depending on their density
            'heatmap-color': [
              'interpolate',
              ['linear'],
              ['heatmap-density'],
              0,
              'rgba(236,222,239,0)',
              0.2,
              'rgb(208,209,230)',
              0.4,
              'rgb(166,189,219)',
              0.6,
              'rgb(103,169,207)',
              0.8,
              'rgb(28,144,153)'
            ],
            // increase radius as zoom increases
            'heatmap-radius': 
            {
              stops: [
                [11, 15],
                [15, 20]
              ]
            },
            // decrease opacity to transition into the circle layer
            'heatmap-opacity': {
              default: 1,
              stops: [
                [14, 1],
                [15, 0]
              ]
            }
          }
        },
        'waterway-label'
      );

      map.current.addLayer(
        {
          id: 'trees-point',
          type: 'circle',
          source: 'trees',
          minzoom: 14,
          paint: {
            // increase the radius of the circle as the zoom level and dbh value increases
            'circle-radius': 30,
            // {
            //   property: 'dbh',
            //   type: 'exponential',
            //   stops: [
            //     [{ zoom: 15, value: 1 }, 5],
            //     [{ zoom: 15, value: 62 }, 10],
            //     [{ zoom: 22, value: 1 }, 20],
            //     [{ zoom: 22, value: 62 }, 50]
            //   ]
            // },
            // 'circle-color': {
            //   property: 'dbh',
            //   type: 'exponential',
            //   stops: [
            //     [0, 'rgba(236,222,239,0)'],
            //     [10, 'rgb(236,222,239)'],
            //     [20, 'rgb(208,209,230)'],
            //     [30, 'rgb(166,189,219)'],
            //     [40, 'rgb(103,169,207)'],
            //     [50, 'rgb(28,144,153)'],
            //     [60, 'rgb(1,108,89)']
            //   ]
            // },
            'circle-stroke-color': 'white',
            'circle-stroke-width': 1,
            'circle-opacity': {
              stops: [
                [14, 0],
                [15, 1]
              ]
            }
          }
        },
        'waterway-label'
      );

      map.current.on('click', 'trees-point', ({ features }) => {
        new mapboxgl.Popup()
          .setLngLat(features[0].geometry.coordinates)
          .setHTML(`<strong>DBH:</strong> ${features[0].properties.dbh}`)
          .addTo(map.current);
      });
    });





  });

  useEffect(() => {
    if (!map.current) return; // wait for map to initialize
    map.current.on('move', () => {
      setLng(map.current.getCenter().lng.toFixed(4));
      setLat(map.current.getCenter().lat.toFixed(4));
      setZoom(map.current.getZoom().toFixed(2));
    });
  });



  return (
    <div>
    <div className="sidebar">
    Longitude: {lng} | Latitude: {lat} | Zoom: {zoom}
    </div>
    <div ref={mapContainer} className="map-container" />
    </div>
    );


}
