import React, { useRef, useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import mapboxgl from '!mapbox-gl'; // eslint-disable-line import/no-webpack-loader-syntax
import axios from 'axios'
import Button from '@mui/material/Button'
import Loading from './Loading';
import './App.css';

mapboxgl.accessToken = 'pk.eyJ1IjoiaWFua2hvdSIsImEiOiJja3U1enMzNHE0enN4Mm9waXNwcDlyeGVpIn0.gz1TKA2H5JfDkMiHkdVxRQ';

export default function App() {
  // update users from Google Sheet
  const mapContainer = useRef(null);
  const map = useRef(null);
  const [users, setUsers] = useState(null);
  const [lng, setLng] = useState(-98.08226562598172);
  const [lat, setLat] = useState(39.67197697791948);
  const [zoom, setZoom] = useState(3);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (!map.current) return; // wait for map to initialize
    map.current.on('move', () => {
      setLng(map.current.getCenter().lng.toFixed(4));
      setLat(map.current.getCenter().lat.toFixed(4));
      setZoom(map.current.getZoom().toFixed(2));
    });
  });

  function addPopup(el, coords) {
    const placeholder = document.createElement('div');
    ReactDOM.render(el, placeholder);

    const marker = new mapboxgl.Popup()
                        .setDOMContent(placeholder)
                        .setLngLat(coords)
                        .addTo(map.current);
  }

  useEffect(() => {
    if (users) return;
    axios.get('https://damp-waters-93180.herokuapp.com/https://script.google.com/macros/s/AKfycbyi1oQ4XTYnCbOKZ5bhIq3mGeTdWxPCAimE8iGzxfxjks8Oip2Z15hijysNxX84GVdALQ/exec', { headers: { 'Access-Control-Allow-Origin': '*' } })
      .then((resp) => {
        setUsers(resp.data)

        // set contains coordinates [long, lat]
        const locCache = new Set()

        const features = {}

        resp.data.forEach((d) => {
          const long = d["Longitude"]
          const lat = d["Latitude"]

          // locCache.forEach(c => {
          //   if ((long - c[0]) ^ 2 + (lat - c[1]) ^ 2 < 0.001) { 

          //   }
          // });

          // console.log({
          //   weight: 200,
          //   name: d['Name (First Last)'],
          //   year: d['Year'],
          //   major: d['Major(s)'],
          //   activity: d['What will you be doing there (work, school, etc.)?']
          // })



          features[d['Email Address']] =  {
            type: "Feature",
            properties: {
              weight: 200,
              name: d['Name (First Last)'],
              year: d['Year'],
              major: d['Major(s)'],
              activity: d['What will you be doing there (work, school, etc.)?']
            },
            geometry: {
              type: "Point",
              coordinates: [
                d["Longitude"], d["Latitude"]
              ]
            }
          }
        })

        const userCollection = {
          type: "FeatureCollection",
          features: Object.values(features)
        }

        if (map.current) return; // initialize map only once

    map.current = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/mapbox/dark-v10',
      center: [lng, lat],
      zoom: zoom
    });

    map.current.on('load', () => {
      map.current.addSource('users', {
        type: 'geojson',
        data: userCollection
      });

      map.current.addLayer(
        {
          id: 'users-heat',
          type: 'heatmap',
          source: 'users',
          maxzoom: 14,
          paint: {
            // increase weight as diameter breast height increases
            'heatmap-weight': {
              type: 'exponential',
              stops: [
                [1, 0],
                [500, 30],
              ]
            },
            // increase intensity as zoom level increases
            'heatmap-intensity': {
              stops: [
                [11, 1],
                [15, 3]
              ]
            },
            // assign color values be applied to points depending on their density
            'heatmap-color': [
              'interpolate',
              ['linear'],
              ['heatmap-density'],
              0,
              'rgba(255,153,153,0)',
              0.2,
              'rgb(255,0,0)',
              0.4,
              'rgb(204,0,0)',
              0.6,
              'rgb(153,0,0)',
              0.8,
              'rgb(102,0,0)'
            ],
            // increase radius as zoom increases
            'heatmap-radius':
            {
              stops: [
                [11, 15],
                [15, 20]
              ]
            },
            // decrease opacity to transition into the circle layer
            'heatmap-opacity': {
              default: 1,
              stops: [
                [14, 1],
                [15, 0]
              ]
            }
          }
        },
        'waterway-label'
      );

      map.current.addLayer(
        {
          id: 'users-point',
          type: 'circle',
          source: 'users',
          minzoom: 12,
          paint: {
            // increase the radius of the circle as the zoom level and weight increases
            'circle-radius':
            {
              property: 'weight',
              type: 'exponential',
              stops: [
                [{ zoom: 15, value: 1 }, 20],
                [{ zoom: 15, value: 62 }, 40],
                [{ zoom: 22, value: 1 }, 80],
                [{ zoom: 22, value: 62 }, 200]
              ]
            },
            'circle-color': {
              property: 'weight',
              type: 'exponential',
              stops: [
                [0, 'rgb(153,0,0)'],
                [10, 'rgb(153,0,0)'],
                [20, 'rgb(153,0,0)'],
                [30, 'rgb(153,0,0)'],
                [40, 'rgb(153,0,0)'],
                [50, 'rgb(153,0,0)'],
                [60, 'rgb(153,0,0)'],
              ]
            },
            'circle-stroke-color': 'white',
            'circle-stroke-width': 2,
            'circle-opacity': {
              stops: [
                [14, 0],
                [15, 1]
              ]
            }
          }
        },
        'waterway-label'
      );

      map.current.on('click', 'users-point', ({ features }) => {
        const { name, year, major, activity } = features[0].properties;
        const myComp = (
          <div className="user-container">
            <h1>{name}</h1>
            <p>Year: {year}</p>
            <p>Major(s): {major}</p>
            <p>What I'm doing here: {activity}</p>
          </div>
        )

        addPopup(myComp, features[0].geometry.coordinates)
        // new mapboxgl.Popup()
        //   .setLngLat(features[0].geometry.coordinates)
        //   .setHTML(
        //     `<div>
        //       <h1>${name}<h1>
        //       <p>Year: ${year}</p>
        //       <p>Major(s): ${major}</p>
        //       <p>What I'm doing here: ${activity}</p>
        //     </div>`
        //   )
        //   .addTo(map.current);
      });
    });

      })
  });

  useEffect(() => {
    setIsLoading(false);
  }, []);

  return isLoading ? <Loading />: (
    <div>
      <div className="sidebar">
        <p>Longitude: {lng} | Latitude: {lat} | Zoom: {zoom}</p>
        <Button className="action_button" size="small" variant="text" href="https://forms.gle/PGtt9gLc54E8R4us5">
    add yourself
  </Button>
      </div>

      <div ref={mapContainer} className="map-container" />

    </div>
    );


}
