import React, { useRef, useEffect, useState } from 'react';
import mapboxgl from '!mapbox-gl'; // eslint-disable-line import/no-webpack-loader-syntax
import axios from 'axios'
import Button from '@mui/material/Button'
import Loading from './Loading';
import './App.css';


mapboxgl.accessToken = 'pk.eyJ1IjoiaWFua2hvdSIsImEiOiJja3U1enMzNHE0enN4Mm9waXNwcDlyeGVpIn0.gz1TKA2H5JfDkMiHkdVxRQ';


//   return isLoading ? <Loading />
//   : <MapBox />
// }

export default function App() {
  // update users from Google Sheet
  const mapContainer = useRef(null);
  const map = useRef(null);
  const [users, setUsers] = useState(null);
  const [lng, setLng] = useState(-98.08226562598172);
  const [lat, setLat] = useState(39.67197697791948);
  const [zoom, setZoom] = useState(3);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (!map.current) return; // wait for map to initialize
    map.current.on('move', () => {
      setLng(map.current.getCenter().lng.toFixed(4));
      setLat(map.current.getCenter().lat.toFixed(4));
      setZoom(map.current.getZoom().toFixed(2));
    });
  });

  useEffect(() => {
    if (users) return;
    axios.get('https://damp-waters-93180.herokuapp.com/https://script.google.com/macros/s/AKfycbyi1oQ4XTYnCbOKZ5bhIq3mGeTdWxPCAimE8iGzxfxjks8Oip2Z15hijysNxX84GVdALQ/exec', { headers: { 'Access-Control-Allow-Origin': '*' } })
      .then((resp) => {
        // console.log(resp.data)
        setUsers(resp.data)
        const features = resp.data.map((d) => {
          return {
            type: "Feature",
            properties: {
              dbh: 200,
              name: d['Name (First Last)'],
              year: d['Year'],
              major: d['Major(s)'],
              activity: d['What will you be doing there (work, school, etc.)?']
            },
            geometry: {
              type: "Point",
              coordinates: [
                d["Longitude"], d["Latitude"]
              ]
            }
          }
        })
        const featureCollection = {
          type: "FeatureCollection",
          features
        }

        if (map.current) return; // initialize map only once

    map.current = new mapboxgl.Map({
      container: mapContainer.current,
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [lng, lat],
      zoom: zoom
    });

    map.current.on('load', () => {
      map.current.addSource('trees', {
        type: 'geojson',
        // data: './trees.geojson'
        data: featureCollection
      });
      // add heatmap layer here
      // add circle layer here

      map.current.addLayer(
        {
          id: 'trees-heat',
          type: 'heatmap',
          source: 'trees',
          maxzoom: 14,
          paint: {
            // increase weight as diameter breast height increases
            'heatmap-weight': {
              // property: 'dbh',
              type: 'exponential',
              stops: [
                [1, 0],
                [500, 30],
              ]
            },
            // increase intensity as zoom level increases
            'heatmap-intensity': {
              stops: [
                [11, 1],
                [15, 3]
              ]
            },
            // assign color values be applied to points depending on their density
            'heatmap-color': [
              'interpolate',
              ['linear'],
              ['heatmap-density'],
              // 0,
              // 'rgba(153,255,153,0)',
              // 0.2,
              // 'rgb(0,255,0)',
              // 0.4,
              // 'rgb(0,204,0)',
              // 0.6,
              // 'rgb(0,153,0)',
              // 0.8,
              // 'rgb(0,102,0)'
              0,
              'rgba(255,153,153,0)',
              0.2,
              'rgb(255,0,0)',
              0.4,
              'rgb(204,0,0)',
              0.6,
              'rgb(153,0,0)',
              0.8,
              'rgb(102,0,0)'
            ],
            // increase radius as zoom increases
            'heatmap-radius':
            {
              stops: [
                [11, 15],
                [15, 20]
              ]
            },
            // decrease opacity to transition into the circle layer
            'heatmap-opacity': {
              default: 1,
              stops: [
                [14, 1],
                [15, 0]
              ]
            }
          }
        },
        'waterway-label'
      );

      map.current.addLayer(
        {
          id: 'trees-point',
          type: 'circle',
          source: 'trees',
          minzoom: 12,
          paint: {
            // increase the radius of the circle as the zoom level and dbh value increases
            'circle-radius':
            {
              property: 'dbh',
              type: 'exponential',
              stops: [
                [{ zoom: 15, value: 1 }, 20],
                [{ zoom: 15, value: 62 }, 40],
                [{ zoom: 22, value: 1 }, 80],
                [{ zoom: 22, value: 62 }, 200]
              ]
            },
            'circle-color': {
              property: 'dbh',
              type: 'exponential',
              stops: [
                // [0, 'rgb(0,153,0)'],
                // [10, 'rgb(0,153,0)'],
                // [20, 'rgb(0,153,0)'],
                // [30, 'rgb(0,153,0)'],
                // [40, 'rgb(0,153,0)'],
                // [50, 'rgb(0,153,0)'],
                // [60, 'rgb(0,153,0)'],
                [0, 'rgb(153,0,0)'],
                [10, 'rgb(153,0,0)'],
                [20, 'rgb(153,0,0)'],
                [30, 'rgb(153,0,0)'],
                [40, 'rgb(153,0,0)'],
                [50, 'rgb(153,0,0)'],
                [60, 'rgb(153,0,0)'],
              ]
            },
            'circle-stroke-color': 'white',
            'circle-stroke-width': 2,
            'circle-opacity': {
              stops: [
                [14, 0],
                [15, 1]
              ]
            }
          }
        },
        'waterway-label'
      );

      map.current.on('click', 'trees-point', ({ features }) => {
        const { name, year, major, activity } = features[0].properties;
        new mapboxgl.Popup()
          .setLngLat(features[0].geometry.coordinates)
          .setHTML(
            `<div>
              <h1>${name}<h1>
              <p>Year: ${year}</p>
              <p>Major(s): ${major}</p>
              <p>What I'm doing here: ${activity}</p>
            </div>`
          )
          .addTo(map.current);
      });
    });

      })
  });

  useEffect(() => {
    setIsLoading(false);
  }, []);

  return (
    <div>
      <div className="sidebar">
        <p>Longitude: {lng} | Latitude: {lat} | Zoom: {zoom}</p>
        <Button className="action_button" variant="outlined" href="https://forms.gle/PGtt9gLc54E8R4us5">
    add yourself
  </Button>
      </div>

      <div ref={mapContainer} className="map-container" />

    </div>
    );


}
