        {
          id: 'trees-point',
          type: 'circle',
          source: 'trees',
          minzoom: 12,
          paint: {
            // increase the radius of the circle as the zoom level and dbh value increases
            'circle-radius':
            {
              property: 'dbh',
              type: 'exponential',
              stops: [
                [{ zoom: 15, value: 1 }, 20],
                [{ zoom: 15, value: 62 }, 40],
                [{ zoom: 22, value: 1 }, 80],
                [{ zoom: 22, value: 62 }, 200]
              ]
