                [10, 'rgb(0,153,0)'],
                [20, 'rgb(0,153,0)'],
                [30, 'rgb(0,153,0)'],
                [40, 'rgb(0,153,0)'],
                [50, 'rgb(0,153,0)'],
                [60, 'rgb(0,153,0)'],
